import { useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs } from "../../Redux/actions";
import { Link } from "react-router-dom";
import DogCard from "../DogCard/DogCard";
import Paginado from "../Paginado/Paginado";
import './Home.css';
import React from "react";


export default function Home (){

    
    const dispatch = useDispatch()
   const allDogs = useSelector((state) => state.dogs)

   const [currentPage, setCurrentPage] = useState(1)
    const [dogsPerPage, /*_setDogCurrentPerPage*/] = useState(8)
    const indexOfLastDog = currentPage * dogsPerPage
    const indexOfFirstDog = indexOfLastDog - dogsPerPage
    const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog)
    const [_orden, setOrden] = useState('')

    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }


useEffect(() => {
    dispatch(getDogs());
}, [dispatch])

function handleClick(e){
    e.preventDefault();
    dispatch(getDogs());
    setCurrentPage(1);
}

return (
    <div className='home'>
        <div className='divNB'></div>
        <ul className='navbar'>
                    <li>
                        <Link to='/'><button className='welcome'><span>Pagina de Bienvenida</span></button></Link>
                    </li>
                    <li>
                        <button className='welcome' onClick={e => { handleClick(e) }}><span>Recargar Perros</span></button>
                    </li>
                    <li>
                        <Link to='/dogs' ><button className='welcome'><span>Crear Perro</span></button></Link>
                    </li>
                </ul>
                <ul className='navbar'></ul>

            <Paginado dogsPerPage={dogsPerPage} allDogs={allDogs.length} paginado={paginado} />
                
                    {currentDogs?.map( (el)=>{
                    return(
                    <div key={el.id}>
                    <Link to={'/home/' + el.id} style={{ textDecoration: 'none' }}>  
                        <DogCard name={el.name} 
                                image={el.image} 
                                temperament={el.temperament} 
                                weight_min={el.weight_min}
                                weight_max={el.weight_max}
                                key={el.id}/> 
                    </Link>
                    </div>
                
                    )
                })}
            
                </div>

        

        )}
