import {
    GET_DOGS
} from '../Redux/actions'



const initialState = {
    dogs: [],
    allDogs: [],
    temperaments: [],
    detail: [],
    breeds: [],

    loader: true

}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_DOGS:
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload
            }}}


export default rootReducer;
