import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getDetails } from '../../Redux/actions';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import './Detail.css';


export default function Detail(props) {
  
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getDetails(props.match.params.id));
  }, [props.match.params.id, dispatch]);

  const myDog = useSelector((state) => state.detail)

  console.log(myDog)
  
 

  return(
        <div className='Dogdetail'>     
            
    {
        myDog.length > 0 ?
    
            <div className='contenedor-detalle-dispel'>
            <h1 > Soy {myDog[0].name}</h1>
            <ul >
                <li>
                    <div>
                        <img src = {myDog[0].img? myDog[0].img : myDog[0].image} alt='' width="250px" height="190px"/>
                    </div>
                </li>
            
            <h3 >Temperament: </h3>
            <ul >
            {myDog[0].createdInDB? myDog[0].temperaments.map((el) => el.name).join(", ")
                : myDog[0].temperament}
            
            </ul>
            <div>
            <h4> Height: </h4>
            <h4> Min: {myDog[0].height_min} cm</h4>
            <h4> Max: {myDog[0].height_max} cm</h4>  
            <h4 >Weight: </h4>
            <h4>Min: {myDog[0].weight_min} kg</h4>
            <h4>Max: {myDog[0].weight_max} kg</h4>
            <h3 >Life_span: {myDog[0].life_span}</h3>
            
            </div>
            </ul>
            <div className="contenedor-glass-detail"></div>
            

        </div>: <p>loading...</p>
    }
     <Link to='/home'><button ><span>Back</span></button></Link>
    </div>
    )
}
