import { useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDogs } from "../../Redux/actions";
import { Link } from "react-router-dom";
import DogCard from "../DogCard/DogCard";
import Paginado from "../Paginado/Paginado";
import './Home.css';
import React from "react";


export default function Home (){

    
    const dispatch = useDispatch()
   const allDogs = useSelector((state) => state.dogs)

   const [currentPage, setCurrentPage] = useState(1)
    const [dogsPerPage, /*_setDogCurrentPerPage*/] = useState(8)
    const indexOfLastDog = currentPage * dogsPerPage
    const indexOfFirstDog = indexOfLastDog - dogsPerPage
    const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog)
    const [_orden, setOrden] = useState('')

    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }


useEffect(() => {
    dispatch(getDogs());
}, [dispatch])

function handleClick(e){
    e.preventDefault();
    dispatch(getDogs());
    setCurrentPage(1);
}

return (
    <div>
        <Link to = "/">CREAR PERRO</Link>
        <h1>API DOGS</h1>
        <button onClick={e=>(handleClick(e))}>
            Volver a cargar todas los perros
        </button>

            <Paginado dogsPerPage={dogsPerPage} allDogs={allDogs.length} paginado={paginado} />
            <nav
        className='pagination is centered mb-6'
        role='navigation'
        aria-label='pagination'
        >

        <a className='pagination-previus' onClick={onPreviusPage}>Anterior</a>
        <a className='pagination-next' onClick={onNextPage}>Siguiente</a>
         <ul className='pagination-list'>
                { pageNumbers && 
                   pageNumbers.map(number =>(
                        <li className='number' key={number}>
                        <a onClick={() => paginado(number)} href={`#`} className={s.aStyle}>{number}</a>
                        </li>
                    ))}
                   </ul>
             </nav>
                
                    {currentDogs?.map( (el)=>{
                    return(
                    <div key={el.id}>
                    <Link to={'/home/' + el.id} style={{ textDecoration: 'none' }}>  
                        <DogCard name={el.name} 
                                image={el.image} 
                                temperament={el.temperament} 
                                weight_min={el.weight_min}
                                weight_max={el.weight_max}
                                key={el.id}/> 
                    </Link>
                    </div>
                
                    )
                })}
            
                </div>

        

        )}
