import React from "react";
import { useState, useEffect } from "react";
import { Link,  useHistory } from "react-router-dom";
import { useDispatch,  useSelector } from "react-redux";
import { postDog, getTemperamentsList } from "../../Redux/actions";




export default function Form(){
    const dispatch = useDispatch();
    const history = useHistory();
    const temperaments = useSelector((state) => state.temperament);
    const [errors, setErrors] = useState({});
    const [button, setButton] = useState(true);


    const [input, setInput] = useState({
        name: "",
        min_height: "",
        max_height: "",
        min_weight: "",
        max_weight: "",
        life_span:  "",
        image: "",
        temperament: []
    })
 

//--------EXPRESIONES REGULARES--------//

    let noEmpty = /\S+/;
    let validateName = /^[a-z]+$/i;
    let validateNum = /^\d+$/;
    let validateLife = /^\d+(?:-\d+)+$/


//--------VALIDACION----------//    

    function validate(input){
     let errors = {}
     if (!noEmpty.test(input.name) || !validateName.test(input.name) || input.name.length < 3) {
      errors.name = "Name required. Only string of more than two characters and without numbers";
      }
      if (!validateNum.test(input.min_height) || parseInt(input.min_height) < 1) {
        errors.min_height = "Number required. Higher than 0.";
      }
      if (!validateNum.test(input.max_height) || parseInt(input.max_height) < parseInt(input.min_height)) {
        errors.max_height = "Number required. Higher than Min Height.";
      }
      if (!validateNum.test(input.min_weight) || parseInt(input.min_weight) < 1) {
        errors.min_weight = "Number required. Higher than 0.";
      }
      if (!validateNum.test(input.max_weight) || parseInt(input.max_weight) < parseInt(input.min_weight)) {
        errors.max_weight = "Number required. Higher than Min Weight.";
      }
      if (!validateLife.test(input.life_span) || parseInt(input.life_span) < 1) {
        errors.life_span = "Two numbers are required separated by a '-'. Higher than 0.";
      }
    

return errors;


    } 

 //--------HANDLERS--------------//   

    function handleChange(e) {
      setInput({
        ...input,
        [e.target.name] : e.target.value
      })

      console.log(input)
      
      setErrors(validate({
        ...input,
        [e.target.name]: e.target.value
    }))
    }

    function handleSelect(e){
      setInput({
        ...input,
        temperaments: [...input.temperament, e.target.value ]
      })
    }

    function handleSubmit(e){
      e.preventDefault();
      dispatch(postDog(input));
      alert("Dog Successfully Create");
      setInput({
        name: "",
        min_height: "",
        max_height: "",
        min_weight: "",
        max_weight: "",
        life_span:  "",
        image: "",
        temperaments: []
      })
      history.push("/home")
    }

    const handleDelete = (el) => {
      setInput({
          ...input,
          temperaments: input.temperament.filter(temp => temp !== el)
      })
  }

  //--------USE EFFECT----------//

     useEffect(()=>{
        dispatch(getTemperamentsList());
        },[dispatch]);
        useEffect(()=>{
          if (input.name.length > 0 && input.min_height.length > 0  && input.max_height.length > 0 && input.min_weight.length > 0 && input.max_weight.length > 0 && input.life_span.length > 0) setButton(false)
          else setButton(true)
      }, [input, setButton]);  



        return (
            <div >
              <br />
                <Link to ="/home">
                <img  src=" " width="70px" height="70px" alt="landing" />
                </Link>
                
                <div >
                  <div >
                <h1 > Create your Dog</h1>
                <form onSubmit={(e) => handleSubmit(e)}>

                    <div>
                        <label>Name: <span >*</span></label>
                        <input
                        type ="text"
                        value = {input.name}
                        name = "name"
                        placeholder="Name..."
                        onChange={(e) =>handleChange(e)}
                        required="obligatorio"
                        />
                        <p>{errors.name}</p>
                    </div>
                    
                    <div>
                      <label>Dog Image: </label>
                      <input 
                      type="text"
                      name="image" 
                      value={input.image} 
                      placeholder="Image Url..." 
                      onChange={handleChange}/>
                     
                    </div>

                    <div>
                       <div>
                        <label>Min Height: <span>*</span></label>
                        <input
                         type="text" 
                         name="min_height"
                         value={input.min_height}
                         placeholder= "Min Height..."
                         onChange={(e)=>handleChange(e)}
                         required="obligatorio"/>
                        <p >{errors.min_height}</p>
                       </div>

                       <div>
                        <label>Max Height: <span >*</span></label>
                        <input 
                        type="text"
                        name="max_height"
                        value={input.max_height} 
                        placeholder="Max Height..."
                        onChange={handleChange}
                        required="obligatorio"/>
                       <p >{errors.max_height}</p>
                       </div>
                    </div>

                    <div>
                      <div>
                        <label>Min Weight: <span >*</span></label>
                        <input 
                        type="text"
                        name="min_weight"
                        value={input.min_weight}
                        placeholder="Min Weight..." 
                        onChange={handleChange}
                        required="obligatorio"/>
                        <p >{errors.min_weight}</p>
                      </div>

                      <div>
                        <label>Max Weight: <span >*</span></label>
                        <input 
                        type="text" 
                        value={input.max_weight}
                        name="max_weight"
                        placeholder="Max Weight..."
                        onChange={handleChange}
                        required="obligatorio"/>
                       <p>{errors.max_weight}</p> 
                      </div>
                    </div>

                   <div>
                    <label>Years of Life: <span>*</span></label>
                    <input 
                    type="text" 
                    name="life_span"
                    value={input.life_span}
                    placeholder="Years of Life..." 
                    onChange={handleChange}
                    required="obligatorio"/>
                   <p >{errors.life_span}</p>
                    </div> 

                    <select onChange={(e) => handleSelect(e)}>
                      <option disabled selected>Temperaments List</option>
                     {temperaments.map((temp) => (
            <option value={temp.name}>{temp.name}</option>
          ))}
        </select>
        <ul><li >{input.temperament.map(el => el + ", ")}</li></ul>
        <p >
									<span > * </span>los campos son obligatorios.
								</p> 
       
        
                <div>
                        {input.temperament.map(el => 
                        <div  key={el} onClick={() => handleDelete(el)}>
                           <div > 
                            <input type="checkbox"/>
                            {`${el}`} 
                          </div> 
                        </div>    
                        )}
                    </div> 
                    <button disabled={button} type="submit">Create Dog</button>
                </form>
                <div>
                      
                    
                </div>
                </div>
                </div>
                


            </div>
        )
}
