import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { getDetails } from '../../Redux/actions';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import './Detail.css';


export default function Detail(props) {
  console.log(props)
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getDetails(props.match.params.id));
  }, [props.match.params.id, dispatch]);

  const myDog = useSelector((state) => state.detail)
 

  return(
        <div className="divDetail">
            <Link to='/home'><button id="home" className="welcome"><span>Inicio</span></button></Link>
            <Link to='/dogs'>
                <button className="welcome"><span>Crear Perro</span></button>
            </Link>
    {
        myDog.length > 0 ?
        <div>
            <h1 claseName="name" > Soy {myDog[0].name}</h1>
            <img src = {myDog[0].img? myDog[0].img : myDog[0].image} alt="" width='100px' height='100'/>
            <h2>Bred_group: {myDog[0].breed_group}</h2>
            <h3>Temperament: </h3>
            <p>{myDog[0].createdInDB? myDog[0].temperaments.map((el) => el.name).join(", ")
                : myDog[0].temperament}
            </p>
            <div>
            <h3>Life_span: {myDog[0].life_span}</h3>
            <h4>Height: </h4>
            <h4> Min: {myDog[0].height_min} cm</h4>
            <h4> Max: {myDog[0].height_max} cm</h4>  
            <h4>Weight: </h4>
            <h4>Min: {myDog[0].weight_min} kg</h4>
            <h4>Max: {myDog[0].weight_max} kg</h4>
            
            </div>

        </div>: <p>loading...</p>
    }
    <Link to= '/home'>
        <button>Volver</button>
    </Link>
    </div>
    )
}
